in = 0.1; %affects to the accuracy of colour, should be around 0.1. However, higher value decrease the calculatin time

xI = interp1(xyz(1,:),xyz(2,:),360:in:830); %x, y and z and spectrum for intermediate of in (nm)
yI = interp1(xyz(1,:),xyz(3,:),360:in:830);
zI = interp1(xyz(1,:),xyz(4,:),360:in:830);
SpC = Ks*interp1(Sp(:,1),Sp(:,2),360:in:830);
YM = 0;
for i = 1:length(SpC)
    if(isnan(SpC(i)))
        SpC(i,2) = 0;
    end
    YM = YM + SpC(i)*yI(i);
end

Xb = 0; %colour formed by base reflectance
Yb = 0;
Zb = 0;

for i = 1:length(yI)
    Xb = Xb + base*SpC(i)*xI(i)/YM;
    Yb = Yb + base*SpC(i)*yI(i)/YM;
    Zb = Zb + base*SpC(i)*zI(i)/YM;
end

X = XYZ(1) - Xb; %colour coordinates that should be formed by Gaussian peak
Y = XYZ(2) - Yb;
Z = XYZ(3) - Zb;

if(X > 0 && Y > 0 && Z >= 0) %Continues calculations only if X-Xb > 0 etc. Otherwise returns colour XYZb

%first trying to form correct colour with peak placement between 420 - 700
%nm with % nm steps

t1 = 5;
n = 420:t1:700;
tulos = zeros(length(n),10);

k = 0.5*(pM-base);
Yk = n(1);
c = max(1, 100*XYZ(3)/(k*1.7));
j = 0;

%colour/Gaussian peak optimization for each peak placement
for i = n
    j = j+1;
    Yk = i; %peak placement
    
   for m = 1:20 %20 optimization rounds for each peak placement
       Xa = 0;
       Ya = 0;
       Za = 0;
       for ii = 1:length(yI) %calculating formed colour by current parameters
           wl = 360 + (ii-1)*in;
           Gaus = k*exp(-((wl-Yk)/c)^2);
           Xa = Xa + Gaus*SpC(ii)*xI(ii)/YM;
           Ya = Ya + Gaus*SpC(ii)*yI(ii)/YM;
           Za = Za + Gaus*SpC(ii)*zI(ii)/YM;
       end
       
       if(Yk < Yra) %changing parameters depending from their value and peak placement          
           kk = max(0.95, min(Z/Za, 1.05));
           k1 = max(0, min(kk*k, pM-base));
           Yaa = (k1/k)*Ya;
           k = k1;
           ck = max(0.95, min(Y/Yaa, 1.05));
           c = max(0, min(ck*c, 500));
       elseif(Yk<Yry)
           kk = max(0.95, min(vk*(Z/Za)+(1-vk)*(Y/Ya), 1.05));
           k1 = max(0, min(kk*k, pM-base));
           Xaa = (k1/k)*Xa;
           k = k1;
           ck = max(0.95, min(X/Xaa, 1.05));
           c = max(0, min(ck*c, 500));
       else
           kk = max(0.95, min(Y/Ya, 1.05));
           k1 = max(0, min(kk*k, pM-base));
           Zaa = (k1/k)*Za;
           k = k1;
           ck = max(0.95, min(Z/Zaa, 1.05));
           c = max(0, min(ck*c, 500));
       end
   end
   
   Xa = 0;
   Ya = 0;
   Za = 0;
   for ii = 1:length(yI) %calculaing formed colour for each peak placement after all optimization rounds
       wl = 360 + (ii-1)*in;
       Gaus = k*exp(-((wl-Yk)/c)^2);
       Xa = Xa + Gaus*SpC(ii)*xI(ii)/YM;
       Ya = Ya + Gaus*SpC(ii)*yI(ii)/YM;
       Za = Za + Gaus*SpC(ii)*zI(ii)/YM;
   end
  
   %storing optimized parameters to Tulos vector
   k1 = min(k*(Y/Ya),pM-base);
   Xa = Xa*(k1/k);
   Ya = Ya*(k1/k);
   Za = Za*(k1/k);
   k = k1;
   Cerror = (abs(XYZ(1)-(Xb+Xa))+abs(XYZ(2)-(Yb+Ya))+abs(XYZ(3)-(Zb+Za)))/sum(XYZ);   
   tulos(j,:) = [Cerror, Xb+Xa,Yb+Ya,Zb+Za, Xb,Yb,Zb, k, Yk, c];  
end

%After going through all peak placements between 5 nm steps choose best
%peak placement
ind = 1;
Ce = tulos(1,1);
for i = 1:length(tulos)
    if(Ce > tulos(i,1))
        Ce = tulos(i,1);
        ind = i;
    end
end
if(ind > 1)
    Ra = tulos(ind-1,:);
else
    Ra = tulos(ind,:);
end
Result = tulos(ind,:);

%Doing same optimization procedure as above but now, between op-5nm and op+5nm, with step size t2 
%, where , op is optimal peak in placement from above 
Yka = Result(9) - t1;
Ykl = Result(9) + t1;
t2 = 0.2;

k = Ra(8);
Yk = Ra(9);
c = Ra(10);
n = Yka:t2:Ykl;
tulos = zeros(length(n),10);
j = 0;

for i = Yka:t2:Ykl 

    j = j+1;
    Yk = i;%peak placement
    
   for m = 1:20 %optimization rounds
       Xa = 0;
       Ya = 0;
       Za = 0;
       for ii = 1:length(yI) %colour with current parameters
           wl = 360 + (ii-1)*in;
           Gaus = k*exp(-((wl-Yk)/c)^2);
           Xa = Xa + Gaus*SpC(ii)*xI(ii)/YM;
           Ya = Ya + Gaus*SpC(ii)*yI(ii)/YM;
           Za = Za + Gaus*SpC(ii)*zI(ii)/YM;
       end
       
       if(Yk < Yra) %changing reflectance parameters based on colour and peak placement
           kk = max(0.995, min(Z/Za, 1.005));
           k1 = max(0, min(kk*k, pM-base));
           Yaa = (k1/k)*Ya;
           k = k1;
           ck = max(0.995, min(Y/Yaa, 1.005));
           c = max(0, min(ck*c, 500));
       elseif(Yk<Yry)
           kk = max(0.995, min(vk*(Z/Za)+(1-vk)*(Y/Ya), 1.005));
           k1 = max(0, min(kk*k, pM-base));
           Xaa = (k1/k)*Xa;
           k = k1;
           ck = max(0.995, min(X/Xaa, 1.005));
           c = max(0, min(ck*c, 500));    
       else
           kk = max(0.995, min(Y/Ya, 1.005));
           k1 = max(0, min(kk*k, pM-base));
           Zaa = (k1/k)*Za;
           k = k1;
           ck = max(0.995, min(Z/Zaa, 1.005));
           c = max(0, min(ck*c, 500));
       end
   end
   
   Xa = 0;
   Ya = 0;
   Za = 0;
   for ii = 1:length(yI)
       wl = 360 + (ii-1)*in;
       Gaus = k*exp(-((wl-Yk)/c)^2);
       Xa = Xa + Gaus*SpC(ii)*xI(ii)/YM;
       Ya = Ya + Gaus*SpC(ii)*yI(ii)/YM;
       Za = Za + Gaus*SpC(ii)*zI(ii)/YM;
   end
  
   k1 = min(k*(Y/Ya),pM-base);  
   Xa = Xa*(k1/k);
   Ya = Ya*(k1/k);
   Za = Za*(k1/k);
   k = k1;
   Cerror = (abs(XYZ(1)-(Xb+Xa))+abs(XYZ(2)-(Yb+Ya))+abs(XYZ(3)-(Zb+Za)))/sum(XYZ);   
   tulos(j,:) = [Cerror, Xb+Xa,Yb+Ya,Zb+Za, Xb,Yb,Zb, k, Yk, c]; %Results
end

%Choosing optimal result = minimum colour error
ind = 1;
Ce = tulos(1,1);
for i = 1:length(tulos)
    if(Ce > tulos(i,1))
        Ce = tulos(i,1);
        ind = i;
    end
end

    Result = tulos(ind,:); %Optimal results

else
    Cerror = (abs(XYZ(1)-Xb)+abs(XYZ(2)-Yb)+abs(XYZ(3)-Zb))/sum(XYZ); 
    Result = [Cerror, Xb,Yb,Zb, Xb,Yb,Zb, 0,0,0]; %Results if colour cannot be produced because of too high base
end
end

